mapper.insert:
INSERT INTO manager (name,account,password,status,created,updated,isDeleted,extra) VALUES
(#{name},#{account},#{password},#{status},#{created},#{updated},#{isDeleted},#{extra});

    SELECT LAST_INSERT_ID()

mapper.update:
UPDATE manager SET updated = UNIX_TIMESTAMP()

    ,name=#{name}


    ,account=#{account}


    ,password=#{password}


    ,status=#{status}


    ,extra=#{extra}


    ,isDeleted=#{isDeleted}

WHERE id=#{id}

UPDATE manager
SET updated = UNIX_TIMESTAMP(),status = '1'
WHERE id = #{id}

mapper.delete:
UPDATE manager SET updated = UNIX_TIMESTAMP()

    ,isDeleted=#{isDeleted}

WHERE id=#{id}

mapper.select:
SELECT * FROM manager
WHERE isDeleted=0

    and name = #{name}


    and account = #{account}


    and password = #{password}


    and status = #{status}
